import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{o as i,c as u,f as e}from"./app-c550aa6e.js";const t={},s=e('<h1 id="linux" tabindex="-1"><a class="header-anchor" href="#linux" aria-hidden="true">#</a> Linux</h1><ul><li><p>linux 常用命令</p><ul><li>基本操作 <ul><li>cd</li><li>mkdir</li><li>mv 移动文件</li><li>ls 查看当前文件夹下的文件名</li><li>ls -l 查看文件夹下的文件详细信息【权限，文件名，文件更新时间，大小（单位为Byte）等】</li></ul></li><li>进程相关 <ul><li>ps -ef 标准格式显示静态进程</li><li>top 动态查看进程</li></ul></li><li>日志相关 <ul><li>linux 三剑客：grep ， sed ，awk</li><li>tail 用于查看动态日志</li></ul></li><li>内存相关 <ul><li>free -h</li></ul></li><li>磁盘文件大小相关 <ul><li>du</li><li>ls -lh 会显示当前文件夹下的所有文件的详细信息，【权限，文件名，文件更新时间，大小（单位为Byte）等】</li></ul></li></ul></li><li><p>查看文件的具体大小，比如多少M</p><ul><li>ls -lh 会显示当前文件夹下的所有文件的详细信息，【权限，文件名，文件更新时间，大小（单位为Byte）等】</li></ul></li><li><p>linux 杀掉一个进程</p><ul><li>kill -9 暴力式杀进程</li><li>kill -15 温和式杀进程，等应用操作完成后再杀掉进程</li></ul></li><li><p>查看当前有哪些Java进程在运行，用什么命令？</p><ul><li>jps</li></ul></li><li><p>想查看一个进程中有哪些线程在运行，如何看？</p><ul><li>首先要通过top或者ps或者jps查看当前进程的进程id</li><li>然后用JDK工具jstack pid，输出jstack文件</li><li>jstack 文件里面就会有当前进程的所有线程信息</li></ul></li><li><p>如何查看一个端口是否被占用</p><ul><li>.netstat -anp |grep 端口号</li></ul></li></ul>',2),a=[s];function p(n,c){return i(),u("div",null,a)}const d=l(t,[["render",p],["__file","Linux.html.vue"]]);export{d as default};
